import { DatePicker } from "react-native-wdpicker";
import React, { Component } from "react";
import {
  View,
  StyleSheet,
  Text,
  Modal,
  TextInput,
  Dimensions,
  Button,
} from "react-native";
export default class ExampleApp extends Component {
  state = {
    pickedDate: null,
  };

  handlePicker = (data) => {
    this.setState({ pickedData: data });
  };

  handlePlaceholder = () =>
    this.state.pickedDate
      ? this.state.pickedDate.toDateString()
      : "No value Selected";

  render() {
    return (
      <View>
        <DatePicker
          onDateChange={this.handleDatePicker}
          value={this.state.pickedDate}
          title="Date Picker"
          placeholder={this.handlePlaceholder()}
          //iOSOnlyProps={{style: {color: 'green'} }}
          //iosPickerMode="date"
          //androidPickerMode="spinner"
          //locale="fr"
          //isNullable
          //disable
        />
      </View>
    );
  }
}
